// Minified JavaScript for the ballot paper form in the Election Post module.
(function($){$(document).ready(function(){var d=$('.election-candidate-preference-select'),disableOptions=function(a,b){var c=a.val(),prev=a.data('previous_value');if(typeof prev!==undefined){b.find('option[value="'+prev+'"][disabled]').removeAttr('disabled')}if(c!==''&&c!=='NONE'){b.find('option[value="'+c+'"]').not(':selected').attr('disabled','disabled')}a.data('previous_value',c)};d.each(function(){var a=$(this),others=d.not(a);disableOptions(a,others);a.change(function(){disableOptions(a,others)})})})}(jQuery));
